targets.expr:
sum by(instance) (
  (rate(pve_network_receive_bytes{instance=~"$instance.*"}[$__rate_interval]) * on(id, instance) group_left(name, type) pve_guest_info) and on(id, instance) pve_up == 1
)

sum by(instance) (
  (rate(pve_network_transmit_bytes{instance=~"$instance.*"}[$__rate_interval]) * on(id, instance) group_left(name, type) pve_guest_info) and on(id, instance) pve_up == 1
)

sum by (instance) (
  ((pve_cpu_usage_ratio{instance=~"$instance.*",id!~"node.*"}) * on(id, instance) group_left(name, type) pve_guest_info and on(id, instance) pve_up == 1)
)

sum by (id,instance) (
  pve_cpu_usage_limit{instance=~"$instance.*",id=~"node.*"} and on(id) pve_node_info and on(id, instance) pve_up == 1
)

sum by (instance) (
  ((pve_memory_usage_bytes{instance=~"$instance.*",id!~"node.*"})  * on(id, instance) group_left(name, type)  pve_guest_info and on(id, instance) pve_up == 1)
)

max(
    sum by (id,instance) (
    ((pve_cpu_usage_limit{instance=~"$instance.*",id=~"$guest_ids"}) * on(id, instance) group_left(name, type) pve_guest_info) and on(id, instance) pve_up == 1
  )
)
