// Tradfri monitor will pass message on all Tradfri state changes
// Code below checks whether the state change is one of the living room light devices
// and passed matching on/off payload to override the Big Timer state
// i.e. Turning on/off one of the lving room ikea lights will also turn on/off
// all the other devices in the living room lights automation.


// Device 65539 = Living Room Big Lamp (Bulb)
// Device 65560 = Living Room Small Lamp (Socket)

var triggerDevices = [65539, 65560];

for (var i = 0; i < triggerDevices.length; i++) {

    // If the state change is for one of the devices in our array
    if (msg.topic == String(triggerDevices[i]) ) {

        // Check if lightbulb key is defined i.e. is this a bulb?
        if (msg.payload.lightbulb !== undefined) {

            // Check if bulb has been turned on or off
            if (msg.payload.lightbulb.isOn == true) {
                var newMsg = { payload: "on" }
            } else {
                var newMsg = { payload: "off" }
            }
        }

        // Check if plug key is defined i.e. is this a plug?
        if (msg.payload.plug !== undefined) {

            // Check if plug has been turned on or off
            if (msg.payload.plug.isOn == true) {
                var newMsg = { payload: "on" }
            } else {
                var newMsg = { payload: "off" }
            }
        }

        // Return new message
        return newMsg;
    }
}

// Device 65539 = Living Room Big Lamp (Bulb)
// Device 65560 = Living Room Small Lamp (Plug Socket)

// Add relevant device IDs to pass to arrays below
// these will be used in newmessage payloads to tell
// Ikea bulb/switch nodes which devices to turn on/off
var bulbTopics = [65539];
var plugTopics = [65560];


// Debounce
// Check when node last triggered and don't execute again within X seconds
// Just in case since Ikea Monitor can trigger on state changes of either
// device so we might get multiple signals.

// Note - we have separate debounce nodes to prevent the update to HA Helper input_bool
// toggle in HA triggering a manual on/off message to Big Timer when the input_bool
// state is updated by this flow turning on/off.
var debounceMillisec = 3000;

var nowTime = new Date();    // var set to date/time now

// Get last time this node was triggered
var lastTrigger = global.get('lastLivingLightsTrigger');

// Set global var to track last trigger time for debouncing
global.set('lastLivingLightsTrigger', nowTime);

// If lastTrigger value exists, calc time since last triggered
// or else set it really high to allow triggering
if (lastTrigger != undefined) {
    var sinceLastTrigger = nowTime.getTime() - lastTrigger.getTime();
} else {
    var sinceLastTrigger = 1000000000000;
}

// ************** DEBUG (must come before 'return') **************
if (global.get('debug') == true) {
    node.warn("LIVING RM LIGHTS: Last Triggered: " + lastTrigger);
    node.warn("LIVING RM LIGHTS: Since Last Trigger: " + sinceLastTrigger);
}
// ****************************************************************

if (sinceLastTrigger < debounceMillisec) {
    // ************** DEBUG (must come before 'return') **************
    if (global.get('debug') == true) {
        node.warn("LIVING RM LIGHTS: Time since last triggered below threshold: " + debounceMillisec);
    }
    // ****************************************************************
    return [null, null];
} else {

    // Parse payload input - needs to be true/false for bulb input messages
    if (msg.payload == "on") {
        var bulbOnOff = true;
    } else if (msg.payload == "off") {
        var bulbOnOff = false;
    }

    // Payload to turn on/off bulbs
    var bulbMsg = {
        "topic": bulbTopics,
            "payload": {
                "onOff": bulbOnOff
            }
    }

    // Payload to turn on/off plugs
    var plugMsg = {
        "topic": plugTopics,
        "payload": msg.payload
    }

    return [bulbMsg, plugMsg];
}

