//--------------------------------------------------------------------
// What we want to do:
// 1. Get Event details
// 2. Parse Event Name for bin colour: bin_colour
//        - save in msg object @ msg.data.colour
// 3. Do datetime calcs 
//      - calc notification off datetime: bin_notify_off_datetime
//      - now vs event start 
//          - if event started and passed bin_notify_off_datetime then:
//              - save bin_notify = false in msg object @ msg.data.notify
//          - if event started but not passed bin_notify_off_datetime then:
//              - save bin_notify = true in msg object @ msg.data.notify
//      - now vs notification off datetime
//          - if bin_notify_off_datetime passed then:
//              - save bin_notify = false in msg object @ msg.data.notify
// 4. Check for offset_reached = true
//      - if NOT passed bin_notify_off_datetime then:
//          - save bin_notify = true in msg object @ msg.data.notify
// 5. Return msg object on Output 1 if bin_notify = true
//    and  Output 1 if bin_notify = false
//    Msg object now conatins extra fields:
//          - bin_colour @ msg.data.colour
//          - bin_notify @ msg.data.notify
// 6. Output 1 goes to a switch node to parse bin_colour and turn on lights
// 7. Output 2 goes to a node to turn off lights.
//--------------------------------------------------------------------

// Note that global debug variable used to determine  if we output any of the debug text below.
// Variable can be set at top of 'Useful Example Flows' tab



// 1. Get Event details
    var bin_event_name = msg.data.event.new_state.attributes.message;
    var bin_event_offset_reached = msg.data.event.new_state.attributes.offset_reached;
    var bin_event_started = msg.payload;
    var bin_event_start_datetime = new Date( msg.data.event.new_state.attributes.start_time );

    // Set initial variable to track notification status
    var bin_notify = false
    // Save this flag in msg data at msg.data.notify
    msg.data["notify"] = false;  

    // ************** DEBUG (must come before 'returns') **************
    if ( global.get('debug') == true ) {
        node.warn("BINS - 1. Event Name: " + bin_event_name );
        node.warn("BINS - 1. Offset Reached: " + bin_event_offset_reached );
        node.warn("BINS - 1. Event Started: " + bin_event_started );
        node.warn("BINS - 1. Start DateTime: " + bin_event_start_datetime );
    }
    // ****************************************************************

//--------------------------------------------------------------------

// 2. Parse Event Name for bin colour: bin_colour
//        - save in msg object @ msg.data.colour

    // Get first 4 characters of event name
    var bin_event_name_subst = bin_event_name.substring(0, 4);

    // Check substring for colour match
    if ( bin_event_name_subst == "Blue") {
        var bin_colour="blue";
    } else if ( bin_event_name_subst == "Brow") {
        var bin_colour="brown";
    } else if ( bin_event_name_subst == "Grey") {
        var bin_colour="grey";
    } else if ( bin_event_name_subst == "Green") {
        var bin_colour="green";
    } else if ( bin_event_name_subst == "Test") {
        var bin_colour="test";
    }

    // Add colour to event message details at msg.data.colour
    msg.data["colour"] = bin_colour;

    // ************** DEBUG (must come before 'returns') **************
    if ( global.get('debug') == true ) {
        //node.warn("BINS - 2. Event Name Substring: " + bin_event_name_subst );
        node.warn("BINS - 2. bin_colour: " + bin_colour );
        //node.warn("BINS - 2. msg.data.colour: " + msg.data.colour );
    }
    // ****************************************************************

//--------------------------------------------------------------------

// 3. Do datetime calcs 
//      - calc notification off datetime: bin_notify_off_datetime
//      - now vs event start 
//          - if event started and passed bin_notify_off_datetime then:
//              - save bin_notify = false in msg object @ msg.data.notify
//          - if event started but NOT passed bin_notify_off_datetime then:
//              - save bin_notify = true in msg object @ msg.data.notify
//      - now vs notification off datetime
//          - if bin_notify_off_datetime passed then:
//              - save bin_notify = false in msg object @ msg.data.notify

    var bin_now_datetime = new Date();    // var set to date/time now


    // Calc notification end datetime = event start + 9 hrs
    var bin_event_start_timestamp = bin_event_start_datetime.getTime();  //start time in unix epoch
    var bin_notify_off_timestamp = bin_event_start_timestamp + (3600*3000); // Add 3 hours to get to 9am on bin day
    var bin_notify_off_datetime = new Date(bin_notify_off_timestamp);


    // Check current datetime against bin_event_start_timestamp
    // If event start passed then check if light should be on i.e. notify if not yet passed
    // Note: getTime() method returns a number that represents the milliseconds between the Unix epoch and the given date.
    var bin_event_start_diff_mili = bin_now_datetime.getTime() - bin_notify_off_datetime.getTime();
    var bin_event_start_diff_h = new Date(bin_event_start_diff_mili).getHours();
    var bin_event_start_diff_m = new Date(bin_event_start_diff_mili).getMinutes();
    var bin_event_start_diff_h = Number((bin_event_start_diff_h<10) ? '0' + bin_event_start_diff_h : bin_event_start_diff_h);
    var bin_event_start_diff_m = Number((bin_event_start_diff_m<10) ? '0' + bin_event_start_diff_m : bin_event_start_diff_m);
    var bin_event_start_diff_hm = bin_event_start_diff_h + ':' + bin_event_start_diff_m;

    if ( bin_event_start_datetime <= bin_now_datetime ) {
        //if ( global.get('debug') == true ) {
        //    node.warn("BINS - Event Start Has Passed");
        //}

        // If event start passed then check if light should be on i.e. notify off not yet passed
        if ( bin_notify_off_datetime <= bin_now_datetime ) {
            //if ( global.get('debug') == true ) {
            //    node.warn("BINS - Event Start Passed & Notify Off Passed - Turn Off Light!");
            //}

            // Set variable to track that notification should be off
            bin_notify = false

            // Save this flag in msg data at msg.data.notify
            msg.data["notify"] = false; 

        } else if ( bin_now_datetime < bin_notify_off_datetime ) {
            //if ( global.get('debug') == true ) {
            //    node.warn("BINS - Event Start Passed & Notify Off NOT Passed - Turn On Light!");
            //}

            // Set variable to track that notification should be on
            bin_notify = true

            // Save this flag in msg data at msg.data.notify
            msg.data["notify"] = true; 
        }

    } else {
        //if ( global.get('debug') == true ) {
        //    node.warn("BINS - Event Starting in (HH:MM): " + bin_event_start_diff_hm);
        //}

    }


    // ************** DEBUG (must come before 'returns') **************
    if ( global.get('debug') == true ) {
        node.warn("BINS - 3a. Current DateTime: " + bin_now_datetime );
        node.warn("BINS - 3a. Notification Off Date Time: " + bin_notify_off_datetime );
        node.warn("BINS - 3a. Event Start Difference (HH:MM): " + bin_event_start_diff_hm );
        node.warn("BINS - 3a. bin_notify: " + bin_notify );
        node.warn("BINS - 3a. msg.data.notify: " + msg.data.notify );
    }
    // ****************************************************************


    // Check current datetime against bin_notify_off_datetime
    // If notify_off passed then turn off light
    var bin_notify_off_diff_mili = bin_now_datetime.getTime() - bin_notify_off_datetime.getTime();
    var bin_notify_off_diff_h = new Date(bin_notify_off_diff_mili).getHours();
    var bin_notify_off_diff_m = new Date(bin_notify_off_diff_mili).getMinutes();
    var bin_notify_off_diff_h = Number((bin_notify_off_diff_h<10) ? '0' + bin_notify_off_diff_h : bin_notify_off_diff_h);
    var bin_notify_off_diff_m = Number((bin_notify_off_diff_m<10) ? '0' + bin_notify_off_diff_m : bin_notify_off_diff_m);
    var bin_notify_off_diff_hm = bin_notify_off_diff_h + ':' + bin_notify_off_diff_m;

    if ( bin_notify_off_datetime <= bin_now_datetime ) {

        //node.warn("BINS - Turn off light!");

        // Set variable to track that notification should be off
        bin_notify = false

        // Save this flag in msg data at msg.data.notify
        msg.data["notify"] = false; 

    } else {

        //node.warn("BINS - Turn Off Light In (HH:MM): " + bin_notify_off_diff_hm);

    }

    // ************** DEBUG (must come before 'returns') **************
    if ( global.get('debug') == true ) {
        node.warn("BINS - 3b. Notification Off Difference (HH:MM): " + bin_notify_off_diff_hm );
        node.warn("BINS - 3b. bin_notify: " + bin_notify );
        node.warn("BINS - 3b. msg.data.notify: " + msg.data.notify );
    }
    // ****************************************************************

//--------------------------------------------------------------------

// 4. Check for offset_reached = true
//      - if NOT passed bin_notify_off_datetime then:
//          - save bin_notify = true in msg object @ msg.data.notify

if ( bin_event_offset_reached == true ) {
    if ( bin_now_datetime < bin_notify_off_datetime ) {

        // Set variable to track that notification should be on
        bin_notify = true

        // Save this flag in msg data at msg.data.notify
        msg.data["notify"] = true;  

    }

}

    // ************** DEBUG (must come before 'returns') **************
    if ( global.get('debug') == true ) {
        node.warn("BINS - 4. Offset Reached: " + bin_event_offset_reached );
        node.warn("BINS - 4. bin_notify: " + bin_notify );
        node.warn("BINS - 4. msg.data.notify: " + msg.data.notify );
    }
    // ****************************************************************


//--------------------------------------------------------------------

// 5. Return msg object on Output 1 if bin_notify = true
//    and  Output 1 if bin_notify = false
//    Msg object now conatins extra fields:
//          - bin_colour @ msg.data.colour
//          - bin_notify @ msg.data.notify

if ( bin_notify == true ) {
    return [ msg, null ];
} else if ( bin_notify == false ) {
    return [ null, msg ];
}

message="Put Out Bin - " + msg.data.colour;
