// Send notifications if: 
// 1. Long distances: 
    // Distance_edge has reached more than distance_far
        // if dist_new has dropped 33% from dist_edge
// 2. short distances (if dist_edge < dist_far):
        // if dist_new has dropped 50% from dist_edge

distance_far=2000;
distance_edge=global.get('proximity_jenna_home_dist_edge');
distance_new=global.get('proximity_jenna_home_dist_new');

if ( parseFloat(distance_edge) > distance_far ) {
   if ( ( parseFloat(distance_new) / parseFloat(distance_edge) ) < 0.33 ) {
       msg.payload = {
           "state":"off",
           "data": {
               "distance":distance_new,
               "send_msg":"yes"

           }
        };
        return msg;
   }  
} else {
    if ( ( parseFloat(distance_new) / parseFloat(distance_edge) ) < 0.5 ) {
       msg.payload = {
           "state":"off",
           "data": {
               "distance":distance_new,
               "send_msg":"yes"

           }

       };
       return msg;
    }
}

