var distMax = flow.get('dist_max');
var tripLong = global.get('nr_proximity_long_trip_distance');
var tripShort = global.get('nr_proximity_alert_enabled_distance');
var percLong = global.get('nr_proximity_alert_send_return_perc_long');
var percShort = global.get('nr_proximity_alert_send_return_perc_short');


if (distMax >= tripLong) {
// Long trip.

    // Current dist returned to alerting zone i.e. X % of max dist?
    if (distNow < (distMax * percLong / 100) ) {
        var notifyTitle = "Proximity Alert: " + destination + " - " + personName + " (" + distNow + "m )";
        var notifyMessage = "Proximity: " + personName + " is " + distNow + "m from " + destination + ".\r\nMaximum trip distance: " + distMax + "m (long trip - long trip boundary @ " + tripLong + "m).\r\nAlert triggered at " + percLong + "% of max distance.\r\nAlert enabled " + tripShort + "m from " + destination + ".";
    }

} else if (distMax >= tripShort) {
// Short trip.

    // Current dist returned to alerting zone i.e. X % of max dist?
    if (distNow < (distMax * percShort / 100)) {
        var notifyTitle = "Proximity Alert: " + destination + " - " + personName + " (" + distNow + "m )";
        var notifyMessage = "Proximity: " + personName + " is " + distNow + "m from " + destination + ".\r\nMaximum trip distance: " + distMax + "m (short trip - long trip boundary @ " + tripLong + "m) \r\nAlert triggered at " + percShort + "% of max distance.\r\nAlert enabled " + tripShort + "m from " + destination + ".";
    }

}

